import React, {useState} from "react";
import {Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import Translate from "../../../../../Translate";
import CustomizedTreeView from "../../../group/treeUi/tree";
import classes from './classifiersModal.module.css'
import ButtonUi from "../../../../../components/buttons/buttonUi";

const ClassifiersModal = props => {
    const [active, setActive] = useState(0)

    const groupsClickHandler = item => {
        setActive(item.id)
    }

    return (
        <Modal isOpen={true} size='md'>
            <ModalHeader>
                <Translate name={'Classifiers'}/>
            </ModalHeader>
            <ModalBody>
                <header className={classes.mBodyHeader}>
                    <ButtonUi
                        key={0}
                        label={<Translate name={'all'}/>}
                        name={'all'}
                        width={'auto'}
                        height={'auto'}
                        margin={'3px 5px'}
                        padding={'2px 8px'}
                        variant={active === 0 ? 'containerd' : 'outlined'}
                        fontSize={'12px'}
                        color={active === 0 ? 'primary' : 'default'}
                        onClick={groupsClickHandler.bind(this, {id: 0, name: 'all'})}
                    />
                    {
                        props.groups ?
                            props.groups.map(
                                item => {

                                    return (
                                        <ButtonUi
                                            key={item.id}
                                            label={item.name}
                                            name={'group'}
                                            width={'auto'}
                                            height={'auto'}
                                            margin={'3px 5px'}
                                            padding={'2px 8px'}
                                            variant={active === item.id ? 'containerd' : 'outlined'}
                                            fontSize={'12px'}
                                            color={active === item.id ? 'primary' : 'default'}
                                            onClick={groupsClickHandler.bind(this, item)}
                                        />
                                    )
                                }
                            )
                            :
                            <p className="text-center info">
                                <Translate name={'Empty'}/>
                            </p>
                    }
                </header>
                {/*<CustomizedTreeView*/}
                {/*    mainId={props.groups[0].id}*/}
                {/*    mainName={props.groups[0].name}*/}
                {/*    data={props.subGroups}*/}
                {/*    movingGroupStatus={props.movingGroupStatus}*/}
                {/*    startMovingGroup={props.startMovingGroup}*/}
                {/*    endeMovingGroup={props.endeMovingGroup}*/}
                {/*    editPosition={props.editPosition}*/}
                {/*/>*/}
            </ModalBody>
            {/*<ModalFooter></ModalFooter>*/}
        </Modal>
    )
}

export default ClassifiersModal
