import React, {Component} from "react";
import {
    Col,
    FormGroup,
    Label,
    Button,
    ModalBody,
    ModalFooter,
    Modal,
    ModalHeader, Input
} from "reactstrap";
import Translate from "../../../Translate";
import {Plus, Trash2, Search} from "react-feather";
import Formsy from 'formsy-react';

class SuplliersAddModal extends Component{
    constructor(props) {
        super(props);
        this.disableButton = this.disableButton.bind(this);
        this.enableButton = this.enableButton.bind(this);
        this.state = { canSubmit: false };
    }

    printBankElement = () => {
        let element = [];
        let length = this.props.setSupplier.tin.length ? this.props.setSupplier.tin.length : 1;
        for (let i = 0; i < length; i++) {
            element.push(
                <FormGroup row key={i}>
                    <Label for={'bank'} sm={3}>
                        {
                            i === 0 ?
                                <Translate name={'bank'}/>
                                :
                                ''
                        }
                    </Label>
                    <Col sm={3} className={'pr-0'}>
                        <Input
                            type={'select'}
                            className={'square'}
                            name={`bank_id-${i}`}
                            value={
                                this.props.setSupplier.tin.length > 0 && this.props.setSupplier.tin[i] ?
                                    this.props.setSupplier.tin[i].bank_id ?
                                        this.props.setSupplier.tin[i].bank_id.id
                                        :
                                        0
                                    :
                                    0
                            }
                            onBlur={
                                event => this.props.checkValue(event.target.name, event.target.value, i)
                            }
                            onChange={
                                event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value, i)
                            }
                        >
                            <option value={0}>choose...</option>
                            {
                                this.props.banks ?
                                    this.props.banks.map(
                                        bank => <option
                                            key={bank.id}
                                            value={bank.id}
                                        >
                                            {bank.name}
                                        </option>
                                    )
                                    :
                                    <option>Empty</option>
                            }
                        </Input>
                        {
                            this.props.checkValueStatus['bank_id'] && this.props.checkValueStatus['bank_id'][i] ?
                                <span className='danger font-small-1'><Translate
                                    name={this.props.checkValueStatus['bank_id'][i]}/></span>
                                :
                                null
                        }
                    </Col>
                    <Col sm={2} className={'pr-0 pl-0'}>
                        <Input
                            type={'select'}
                            className={'square border-left-0'}
                            name={`currency_id-${i}`}
                            value={
                                this.props.setSupplier.tin && this.props.setSupplier.tin[i] ?
                                    this.props.setSupplier.tin[i].currency_id ?
                                        this.props.setSupplier.tin[i].currency_id.id
                                        :
                                        0
                                    :
                                    0
                            }
                            onBlur={
                                event => this.props.checkValue(event.target.name, event.target.value, i)
                            }
                            onChange={
                                event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value, i)
                            }
                        >
                            <option value={0}>choose...</option>
                            {
                                this.props.currency ?
                                    this.props.currency.map(
                                        item => <option
                                            key={item.id}
                                            value={item.id}
                                        >
                                            {item.short}
                                        </option>
                                    )
                                    :
                                    <option>Empty</option>
                            }
                        </Input>
                        {
                            this.props.checkValueStatus['currency_id'] && this.props.checkValueStatus['currency_id'][i] ?
                                <span className='danger font-small-1'><Translate
                                    name={this.props.checkValueStatus['currency_id'][i]}/></span>
                                :
                                null
                        }
                    </Col>
                    <Col sm={4} className={'pl-0'}>
                        <div className={'position-relative has-icon-right'}>
                            <input
                                className={'form-control square border-left-0'}
                                type={'text'}
                                id={'bank1'}
                                placeholder={'currency'}
                                name={`tin_value-${i}`}
                                value={
                                    this.props.setSupplier.tin && this.props.setSupplier.tin[i] ?
                                        this.props.setSupplier.tin[i].tin_value ?
                                            this.props.setSupplier.tin[i].tin_value
                                            :
                                            ''
                                        :
                                        ''
                                }
                                onBlur={
                                    event => this.props.checkValue(event.target.name, event.target.value, i)
                                }
                                onChange={
                                    event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value, i)
                                }
                            />
                            {
                                <div className={'form-control-position'}>
                                    {
                                        i + 1 === length ?
                                            <Plus
                                                size={20}
                                                className={'success cursor-pointer'}
                                                onClick={
                                                    () => this.props.addTin({}, i)
                                                }
                                            />
                                            :
                                            <Trash2
                                                size={20}
                                                className={'danger cursor-pointer'}
                                                onClick={
                                                    () => this.props.reduceTin(i)
                                                }
                                            />
                                    }
                                </div>
                            }
                        </div>
                        {
                            this.props.checkValueStatus['tin_value'] && this.props.checkValueStatus['tin_value'][i] ?
                                <span className='danger font-small-1'><Translate
                                    name={this.props.checkValueStatus['tin_value'][i]}/></span>
                                :
                                null
                        }
                    </Col>
                </FormGroup>
            )
        }

        return element
    }

    printPhoneElement = () => {
        let element = [];
        let length = this.props.setSupplier.phone.length ? this.props.setSupplier.phone.length : 1;
        for (let i = 0; i < length; i++) {
            element.push(
                <FormGroup row key={i}>
                    <Label for={'phone'} sm={3}>
                        {
                            i === 0 ?
                                <Translate name={'phone'}/>
                                :
                                ''
                        }
                    </Label>
                    <Col sm={9}>
                        <div className={"position-relative has-icon-right"}>
                            <input
                                type={"tel"}
                                className={"form-control square"}
                                placeholder={'example: 098 --- ---'}
                                name={`phone-${i}`}
                                value={this.props.setSupplier.phone[i] ? this.props.setSupplier.phone[i].phone : ''}
                                onBlur={
                                    event => this.props.checkValue(event.target.name, event.target.value, i)
                                }
                                onChange={
                                    event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value, i)
                                }
                            />
                            <div className={"form-control-position"}>
                                {
                                    i + 1 === length ?
                                        <Plus
                                            size={20}
                                            className={'success cursor-pointer'}
                                            onClick={
                                                () => this.props.addPhone(null, i)
                                            }
                                        />
                                        :
                                        <Trash2
                                            size={20}
                                            className={'danger cursor-pointer'}
                                            onClick={
                                                () => this.props.reducePhone(i)
                                            }
                                        />
                                }
                            </div>
                        </div>
                        {
                            this.props.checkValueStatus['phone'] && this.props.checkValueStatus['phone'][i] ?
                                <span className='danger font-small-1'><Translate
                                    name={this.props.checkValueStatus['phone'][i]}/></span>
                                :
                                null
                        }
                    </Col>
                </FormGroup>
            )
        }

        return element;
    }

   render() {
       return (
           <Modal
               isOpen={this.props.isOpen}
               size='lg'
               toggle={
                   () => this.props.openSuppliersAddModal()
               }
           >
               <ModalHeader>
                   <Translate name={`${this.props.modalType}Supplier`}/>
               </ModalHeader>
               <ModalBody>
                   <Formsy >

                       <FormGroup row>
                           <Label for={'firstName'} sm={3}>
                               <Translate name={'name'}/>
                           </Label>
                           <Col sm={7} className={'pr-0'}>
                               <input
                                   id={'firstName'}
                                   className={`form-control square ${this.props.checkValueStatus['name'] ? 'is-invalid' : null}`}
                                   type={'text'}
                                   name={'name'}
                                   value={this.props.setSupplier.name}
                                   onBlur={
                                       (event => this.props.checkValue(event.target.name, event.target.value))
                                   }
                                   onChange={
                                       event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value)
                                   }
                               />
                               {
                                   this.props.checkValueStatus['name'] ?
                                       <span className='danger font-small-1'><Translate
                                           name={this.props.checkValueStatus.name}/></span>
                                       :
                                       null
                               }
                           </Col>
                           <Col sm={2} className={'pl-0'}>
                               <Input
                                   className={'square border-left-0'}
                                   type={'select'}
                                   name={'type'}
                                   value={+this.props.setSupplier.type || 0}
                                   onChange={
                                       event => this.props.setSuppliersAddModalValue(event.target.name, +event.target.value)
                                   }
                               >
                                   <option value={0}>choose...</option>
                                   {
                                       this.props.companyTypes ?
                                           this.props.companyTypes.map(
                                               type => {

                                                   return (
                                                       <option
                                                           key={type.id}
                                                           value={type.value}
                                                       >
                                                           {type.name}
                                                       </option>
                                                   )
                                               }
                                           )
                                           :
                                           <option>Empty</option>
                                   }
                               </Input>
                           </Col>
                       </FormGroup>

                       <FormGroup row>
                           <Label for={'hh'} sm={3}>
                               <Translate name={'hh'}/>
                           </Label>
                           <Col sm={9}>
                               <input
                                   id={'hh'}
                                   className={`form-control square ${this.props.checkValueStatus['hh'] ? 'is-invalid' : null}`}
                                   type={'text'}
                                   name={'hh'}
                                   value={this.props.setSupplier.hh}
                                   onBlur={
                                       (event => this.props.checkValue(event.target.name, event.target.value))
                                   }
                                   onChange={
                                       event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value)
                                   }
                               />
                               {
                                   this.props.checkValueStatus['hh'] ?
                                       <span className='danger font-small-1'><Translate
                                           name={this.props.checkValueStatus['hh']}/></span>
                                       :
                                       null
                               }
                           </Col>
                       </FormGroup>

                       <FormGroup row>
                           <Label for={'hvhh'} sm={3}>
                               <Translate name={'hvhh'}/>
                           </Label>
                           <Col sm={9}>
                               <div className={"position-relative has-icon-right"}>
                                   <input
                                       id={'hvhh'}
                                       className={`form-control square`}
                                       type={'text'}
                                       name={'hvhh'}
                                       value={this.props.setSupplier.hvhh}
                                       onBlur={
                                           (event => this.props.checkValue(event.target.name, event.target.value))
                                       }
                                       onChange={
                                           event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value)
                                       }
                                   />
                                   <div className={"form-control-position"}>
                                       <Search
                                           size={20}
                                           className={'info cursor-pointer'}
                                           onBlur={
                                               (event => this.props.checkValue(event.target.name, event.target.value))
                                           }
                                           onClick={
                                               () => this.props.searchRequisite(this.props.setSupplier.hvhh)
                                           }
                                       />
                                   </div>
                               </div>
                               {
                                   this.props.checkValueStatus['hvhh'] ?
                                       <span className='danger font-small-1'><Translate
                                           name={this.props.checkValueStatus['hvhh']}/></span>
                                       :
                                       null
                               }
                           </Col>
                       </FormGroup>

                       <FormGroup row>
                           <Label for={'address'} sm={3}>
                               <Translate name={'address'}/>
                           </Label>
                           <Col sm={9}>
                               <input
                                   id={'address'}
                                   className={`form-control square ${this.props.checkValueStatus['address'] ? 'is-invalid' : null}`}
                                   type={'text'}
                                   name={'address'}
                                   value={this.props.setSupplier.address}
                                   onBlur={
                                       (event => this.props.checkValue(event.target.name, event.target.value))
                                   }
                                   onChange={
                                       event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value)
                                   }
                               />
                               {
                                   this.props.checkValueStatus['address'] ?
                                       <span className='danger font-small-1'><Translate
                                           name={this.props.checkValueStatus['address']}/></span>
                                       :
                                       null
                               }
                           </Col>
                       </FormGroup>

                       {this.printBankElement()}

                       <FormGroup row>
                           <Label for={'director'} sm={3}>
                               <Translate name={'director'}/>
                           </Label>
                           <Col sm={9}>
                               <input
                                   className={`form-control square ${this.props.checkValueStatus['director'] ? 'is-invalid' : null}`}
                                   type={'text'}
                                   id={'director'}
                                   name={'director'}
                                   onBlur={
                                       (event => this.props.checkValue(event.target.name, event.target.value))
                                   }
                                   value={
                                       this.props.setSupplier.director ?
                                           this.props.setSupplier.director
                                           :
                                           ''
                                   }
                                   onChange={
                                       event => this.props.setSuppliersAddModalValue(event.target.name, event.target.value)
                                   }
                               />
                               {
                                   this.props.checkValueStatus['director'] ?
                                       <span className='danger font-small-1'><Translate
                                           name={this.props.checkValueStatus['director']}/></span>
                                       :
                                       null
                               }
                           </Col>
                       </FormGroup>

                       {
                           this.printPhoneElement()
                       }

                   </Formsy >
               </ModalBody>
               <ModalFooter>
                   {
                       this.props.setSupplier.name.length && this.props.setSupplier.hh.length && this.props.setSupplier.hvhh.length && this.props.setSupplier.address.length && this.props.setSupplier.tin.length > 0 && this.props.setSupplier.director.length && this.props.setSupplier.phone.length && this.props.setSupplier.type ?
                           <Button
                               color="primary"
                               className="btn-square"
                               outline type="submit"
                               onClick={

                                   () => {

                                       this.props.modalType === 'add' ?
                                           this.props.fetchSuppliers(this.props.setSupplier)
                                           :
                                           this.props.supplierActions('edit', this.props.setSupplier)

                                   }
                               }
                           >
                               <Translate name={'confirm'}/>
                           </Button>
                           :
                           <span className='danger font-small-3'><Translate name={'The entire field is required'}/></span>
                   }
               </ModalFooter>
           </Modal>
       )
   }
}

export default SuplliersAddModal;
