import React  from "react";
import {connect} from "react-redux";

class LocalizeInput extends React.Component {
    render() {
        let props = this.props
        console.log(this.props)
        return (
            <div>
                {props.languages.map(function (lang) {

                    return <input
                        className={`form-control  ${props.errors ? 'is-invalid' : ''} ${lang.code !== props.modalLang ? 'd-none' : ''}`}
                        type="text"
                        value={props.name && props.name[props.modalLang] ? props.name[props.modalLang] : ""}
                        onChange={event => props.setValue("name", event.target.value, props.modalLang)}

                    />
                })}
            </div>
        );
    }
}


const mapStateToProps = state => {
    return {
        languages: state.languages.languages,
        modalLang:state.languages.modalLang,
    }
};

export default connect(mapStateToProps)(LocalizeInput);
