import React, {Component, useState} from "react";
import {Row,Col, ListGroup, ListGroupItem} from "reactstrap";
import {ChevronDown, ChevronRight,Plus,Edit,Trash2} from "react-feather";
import Translate from "../../../Translate";
import * as Icon from "react-feather";
function SubMenu(props) {
    const [open, setOpen] = useState(false);
    function handleOpen() {
        console.log(open)
        setOpen(!open)
    }
    function SubMenuItem(props) {

        if (open && props.data) {
            return <SubMenu data={props.data}/>
        }
        return <div></div>
    }
    function Chevron(props) {

        if(props.data) {
            if (!open) {
                return <ChevronRight size={18} className="mr-2" onClick={() => handleOpen()}/>
            }
            return <ChevronDown size={18} className="mr-2" onClick={() => handleOpen()}/>
        }
        return <div></div>
    }

    if (props.data.length > 0) {
        return <div flush>
            {props.data.map((value, index) => {
                return <div >

                    <Row sm="12">
                        <Chevron data={value.children}/>
                        {value.name}
                        <Plus size={18} className="mr-2" />
                        <Edit size={18} className="mr-2" />
                        <Trash2 size={18} className="mr-2" />
                    </Row>
                    <SubMenuItem data={value.children} />
                </div>
            })}

        </div>

    } else {
        return <div></div>
    }
};

class ListGroupDisabled extends Component {
    constructor(props) {
        super(props)

    }

    render() {
        console.log(this.props.data)
        return (
            <div className="todo-app-content-detail">
                <Row>
                    <Col className="">
                        <Icon.Star
                            size={20}
                            className="my-2 mx-1"
                            style={{ color: "#495057" }}
                        />
                        <Icon.AlertCircle
                            size={20}
                            className="my-2 mx-1"
                            style={{ color:"#FF586B"  }}
                        />
                    </Col>
                    <Col className="text-right">
                        <Button className="btn-outline-success mr-1 mt-1" size="sm" >
                            <Icon.Check size={16} />
                        </Button>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default ListGroupDisabled;
