const initialState = {
    sidebarCollapsed: localStorage.getItem('menuType') || true
}

export default function sidebarCollapsed(state = initialState, action) {

    switch (action) {

        case 'SIDEBAR_COLLAPSED':
            return {
                ...state, sidebarCollapsed: localStorage.getItem('menuType')
            }
        default: return state;
    }
}
