import React, { useState } from "react";
import {Badge} from "reactstrap";
import {connect} from "react-redux";

function LocalizeTab(props){
    return (
        <div>
            {props.languages.map(function (lang) {
                console.log(props.group.name)
                return <input
                    className={`form-control  ${props.errors? 'is-invalid':''} ${lang.code!==props.modalLang?'d-none':''}`}
                    type="text"
                    id="name"
                    value={props.group.name && props.group.name[lang.code]?props.group.name[lang.code]:""}
                    onChange={event => props.setValue("name",event.target.value,props.modalLang)}

                />
            })}
        </div>
    );
}


const mapStateToProps = state => {
    return {
        languages: state.languages.languages,
        modalLang:state.languages.modalLang
    }
};

export default connect(mapStateToProps)(LocalizeTab);
