import {
    ADD_TRANSLATION,
    INITIALIZE,
    SET_ACTIVE_LANGUAGE
} from "./actionTypes";
import { initialize,addTranslation } from 'react-localize-redux';
import { translations } from './translation'

const INIT_STATE = {
    user: getSession('user'),
    token: getSession('token'),
    loading: false,
    success: false,
    fail: false,
    errors: {},
};
addTranslation(translations)


export default (state = languages, action) => {

    switch (action.type) {
        case ADD_TRANSLATION:
            addTranslation(translations)
            return {
                loading: true,
            };
        case INITIALIZE:
            (languages, { defaultLanguage: 'fr' })
            return {
                initialize: true,
            };
        default:
            return {...state};
    }
};
