import React, { Component } from "react";
import {Col, Card, CardBody, Modal, ModalHeader, ModalBody, ModalFooter, Button, Table} from "reactstrap";
import TableComponent from './categoryTable';
import AddButton from "../../../components/buttons/addButton";
import Translate from "../../../Translate";
import ModalComponent from "./categoryModal";
// import ModalComponent from './userModal';
// import Translate from "../../../Translate";
// import AddButton from "../../../components/buttons/addButton";


class UserContainer extends Component {
    constructor(props) {
        super(props)
        this.props.getCategories()

    }
    toggle(type,id) {
        this.props.categoryModal(type,!this.props.modal[type])
        if(type==="add"){
            this.props.setModalValues({'parent_id':id})
        }else{
            this.props.setModalValues({'id':id})
        }
    }

    render() {
        console.log(this.props.modal.add)
            return(
                <Col sm="12">
                    <Card>
                        <CardBody>
                            <AddButton perm = {this.props.perm} onClick={() => this.toggle()} />{" "}
                            <TableComponent
                                data = {this.props.categories}
                                toggle={this.toggle}
                                // modal={this.props.modal}
                                // setModalValues={this.props.setModalValues}
                            />
                        </CardBody>
                    </Card>
                    <Modal isOpen={this.props.modal.add} toggle={()=>this.toggle("add")}  size="md">
                        <ModalHeader toggle={()=>this.toggle()}><Translate name="addCategory"/></ModalHeader>
                        <ModalComponent {...this.props} />
                    </Modal>
                    <Modal isOpen={this.props.modal.edit} toggle={()=>this.toggle("edit")}  size="md">
                        <ModalHeader toggle={()=>this.toggle("edit")}><Translate name="editPosition"/></ModalHeader>
                        <ModalComponent {...this.props}/>
                    </Modal>
                    <Modal isOpen={this.props.modal.delete} toggle={()=>this.toggle("delete")}  size="md">
                        <ModalHeader toggle={()=>this.toggle("delete")}><Translate name="deletePosition"/></ModalHeader>
                        <ModalBody>Դուք համոզված ե՞ք ջնջել</ModalBody>
                        <ModalFooter>
                            <Button color="primary" type="submit" >
                                <Translate name="confirm"/>
                            </Button>
                        </ModalFooter>
                    </Modal>

                </Col>

            )

    }
}


export default UserContainer;
