import {
    GET_USERS_REQUEST, GET_USERS_FAIL, GET_USERS_SUCCESS,
    GET_USER_REQUEST, GET_USER_FAIL, GET_USER_SUCCESS,
    ADD_USER_REQUEST, ADD_USER_FAIL, ADD_USER_SUCCESS,
    EDIT_USER_REQUEST, EDIT_USER_FAIL, EDIT_USER_SUCCESS,
    DELETE_USER_REQUEST, DELETE_USER_FAIL, DELETE_USER_SUCCESS,
    SET_MODAL_VALUES,TOGGLE_MODAL
} from "./actionTypes";


const INIT_STATE = {
    users: [],
    user: {},
    modal: {},
    required: [
        "firstname",
        "lastname",
        "username",
        "password",
    ],
    loading: false,
    success: false,
    fail: false,
    errors: {}
};
const checkRequiredFields = (state) => {
    state.required.forEach(function(val, index){
        if(!state.user[val] || state.user[val]===""){
           state.errors[val]= "reqired"
        }
    })
    return state.errors;
}

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_USERS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                modal: {},
                fail: false,
                errors: {},
            };
        case GET_USERS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                modal: {},
                fail: true,
            };
        case GET_USERS_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                users: JSON.parse(action.result.data),
                modal: {},
                errors: {},
            };
        case GET_USER_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_USER_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_USER_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                user: JSON.parse(action.result.data)[0],
                errors: {},
            };
        case ADD_USER_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors:checkRequiredFields(state)
            }
        case ADD_USER_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_USER_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                users: [
                    ...state.users,
                    JSON.parse(action.result.data)[0]

                ]
            };
        case EDIT_USER_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors:checkRequiredFields(state)
            }
        case EDIT_USER_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case EDIT_USER_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                user: {},
                users: JSON.parse(action.result.data)
            };
        case DELETE_USER_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case DELETE_USER_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case DELETE_USER_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                user: {},
                users: JSON.parse(action.result.data)
            };

        case SET_MODAL_VALUES:
            state.user.active = (state.user.active)?state.user.active:0;
            
            state.user[action.key] = action.value;
            return {
                ...state,
            };
        case TOGGLE_MODAL:
            let newModal = {};
            newModal[action.modalType] = !state.modal[action.modalType];
            let user = state.user
            if(action.obj){
                user = (action.modalType==="edit")?{
                    ...state.user,
                    ...action.obj
                }:{
                    ...action.obj
                }
            }

            return {
                ...state,
                modal: {
                    ...state.modal,
                    ...newModal
                },
                user,
                errors: {}
            }
        default:
            return {...state};
    }
};
