import React, { useState } from 'react';
import { Collapse, Button, CardBody, Card } from 'reactstrap';
import classes from './advancedSearch.module.css'
import Translate from "../../../../../Translate";
import * as Icon from 'react-feather';
import SearchIcon from '@material-ui/icons/Search';
import {Col, Row} from "reactstrap";
import CheckboxesUi from "../../../../../components/checkBoxUI/checkBoxUI";

const AdvancedSearch = (props) => {
    const [isOpen, setIsOpen] = useState(false);
    const [type, setType] =useState(false)

    const toggle = () => {
        setIsOpen(!isOpen)
        setType(!type)
    };

    return (
        <div>
            <Button className={classes.searchBtn} color={type ? 'success' : 'primary'} onClick={toggle} style={{ marginBottom: '1rem' }}>
                <Translate name={props.buttonName}/> <SearchIcon className={classes.icon}/>
            </Button>
            <Collapse isOpen={isOpen}>
                <Card>
                    <CardBody>
                       <Row>
                           <Col md={3}>
                               <h5><Translate name={'Search settings'}/></h5>
                               <hr/>
                               {
                                   Object.keys(props.searchItemsKey).map(
                                       (item, index) => {

                                           return (
                                               <p key={index}>
                                                   <CheckboxesUi
                                                       checked={false}
                                                       label={item}
                                                   />
                                               </p>
                                           )
                                       }
                                   )
                               }
                           </Col>
                           <Col md={3}>
                               <h5><Translate name={'filters'}/></h5>
                           </Col>
                           <Col md={3}>md 3</Col>
                       </Row>
                    </CardBody>
                </Card>
            </Collapse>
        </div>
    );
}

export default AdvancedSearch;
