import {ADD_FILE} from "./actionTypes";

const initialState = {
    exel: false,
    file: null,
    errorType: null,
    uploadText: ''
};

export default function importReducer(state = initialState, action) {

    switch (action.type) {

        case ADD_FILE:
            return {
                ...state,
                [action.name]: action.value
            }
        default: return state;
    }
}
