import {
    LOGIN_REQUEST,
    LOGIN_REQUEST_FAIL,
    LOGIN_REQUEST_SUCCESS,
} from "./actionTypes";

import {menuAPI} from "../../utility/menu";
export const menuRequestLoad =  () => {
    return {
        type: LOGIN_REQUEST,
    };
}

export const menuReceiveLoad = (data) => {
    return {
        type: LOGIN_REQUEST_SUCCESS,
        // payload: data
    }
}
export const login = (form, redirectPath) => {

    console.log(form,redirectPath)
    return (dispatch) => {
        dispatch(menuRequestLoad());
        return menuAPI.get()
            .then(data => {
                console.log(data)
                return dispatch(menuReceiveLoad(data))
            });
    }

};
