import React, { Component } from "react";
import {  Col, Card, CardBody, Modal, ModalHeader } from "reactstrap";
import TableComponent from './categoryTable';
import AddButton from "../../../components/buttons/addButton";
import Translate from "../../../Translate";
import ModalComponent from "./categoryModal";
// import ModalComponent from './userModal';
// import Translate from "../../../Translate";
// import AddButton from "../../../components/buttons/addButton";


class UserContainer extends Component {
    constructor(props) {
        super(props)
        this.props.getCategories()

    }
    toggle(type,id) {
        this.props.categoryModal(type,!this.props.modal[type])
        this.props.setModalValues({'parent_id':id})
        if(type==="edit" && id){
            this.props.getCategory(id)
        }
    }

    render() {
        console.log(this.props.modal.add)
            return(
                <Col sm="12">
                    <Card>
                        <CardBody>
                            <AddButton perm = {this.props.perm} onClick={() => this.toggle("add",0)} />{" "}
                            <TableComponent
                                data = {this.props.categories}
                                toggle={this.props.categoryModal}
                                modal={this.props.modal}
                                setModalValues={this.props.setModalValues}
                            />
                        </CardBody>
                    </Card>
                    <Modal isOpen={this.props.modal.add} toggle={()=>this.toggle("add")}  size="md">
                        <ModalHeader toggle={()=>this.toggle("add")}><Translate name="addCategory"/></ModalHeader>
                        <ModalComponent {...this.props} />
                    </Modal>


                </Col>

            )

    }
}


export default UserContainer;
