import {
    GET_POSITIONS_REQUEST,GET_POSITIONS_FAIL,GET_POSITIONS_SUCCESS,
    SET_MODAL_VALUES,
    HANDLE
} from "./actionTypes";
import {
    ADD_USER_FAIL,
    ADD_USER_REQUEST,
    ADD_USER_SUCCESS,
    DELETE_USER_FAIL,
    DELETE_USER_REQUEST,
    DELETE_USER_SUCCESS,
    EDIT_USER_FAIL,
    EDIT_USER_REQUEST,
    EDIT_USER_SUCCESS,
    GET_USER_FAIL,
    GET_USER_REQUEST,
    GET_USER_SUCCESS
} from "../users/actionTypes";

let cols =  'id,name';

export const getPositions = () => {
    return {
        types: [GET_POSITIONS_REQUEST,GET_POSITIONS_FAIL,GET_POSITIONS_SUCCESS],
        promise: (apiClient) => apiClient.get(`User/Position`, JSON.stringify({ cols }))
    }
}


export const getPosition = ( id ) => {
    return {
        types: [GET_POSITION_REQUEST,GET_POSITION_FAIL,GET_POSITIONS_SUCCESS],
        promise: (apiClient) => apiClient.get(`User/Users`, JSON.stringify({ param:{id} }))
    }
};

export const addUser = (data) => {
    let types = [ADD_USER_REQUEST,ADD_USER_FAIL,ADD_USER_SUCCESS];
    let promise = (apiClient) => apiClient.post(`User/Users`,JSON.stringify({data,cols}))
    if(data.id){
        types = [EDIT_USER_REQUEST,EDIT_USER_FAIL,EDIT_USER_SUCCESS];
        promise = (apiClient) => apiClient.put(`User/Users`,JSON.stringify({data,cols,id:data.id}));
    }
    return {
        types: types,
        promise: promise
    }
};

export const deleteUser = ( id ) => {
    return {
        types: [DELETE_USER_REQUEST,DELETE_USER_FAIL,DELETE_USER_SUCCESS],
        promise: (apiClient) => apiClient.delete(`User/Users`, JSON.stringify({ id,cols }))
    }
};

export const handle = (name,parent) => {
    return {
        type: HANDLE,
        name,
        parent,
    }
}

export const positionModal = (type,modal) => {
    return {
        type: type.toUpperCase()+'_MODAL',
        modal
    }
}

export const setModalValues = (key,value) => {
    return {
        type: SET_MODAL_VALUES,
        key,
        value
    }
}
