import React, { Component } from "react";
import {
    TabContent,
    TabPane,
    Nav,
    NavItem,
    NavLink,
    Row,
    Col, FormGroup, Label, Input
} from "reactstrap";
import classnames from "classnames";
import Translate from "../../../Translate";
import MultiSelect from "../../../components/select/multiSelect"
import {RenameKeys,ObjectToArray} from "../../../utility/utils"
import {setModalValues} from "../../../redux/products/actions";
class TabsBorderBottom extends Component {

    constructor(props) {
        super(props)
        this.state = {
            activeTab: "code"
        };
        this.props.supplierActions("getAll");
        this.props.groupActions("getAll");

    }
    toggle = tab => {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    };
    render() {
        return (
            <div>
                <Nav tabs className="nav-border-bottom">
                    {Object.keys(this.props.groups).map((key) => {
                        return <NavItem>
                            <NavLink
                                className={classnames({
                                    active: this.state.activeTab === key
                                })}
                                onClick={() => {
                                    this.toggle(key);
                                }}
                            >
                                {this.props.groups[key].name[this.props.lang.active]}
                            </NavLink>
                        </NavItem>
                    })}

                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "suppliers"
                            })}
                            onClick={() => {
                                this.toggle("suppliers");
                            }}
                        >
                            <Translate name={"suppliers"}/>
                        </NavLink>
                    </NavItem>

                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "code"
                            })}
                            onClick={() => {
                                this.toggle("code");
                            }}
                        >
                            <Translate name={"code"}/>
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "description"
                            })}
                            onClick={() => {
                                this.toggle("description");
                            }}
                        >
                            <Translate name={"description"}/>
                        </NavLink>
                    </NavItem>
                </Nav>
                <TabContent activeTab={this.state.activeTab}>
                    {Object.keys(this.props.groups).map((key) => {
                        console.log(this.props.groups[key].subGroup)
                        return <GroupTabContent />
                    })}

                    <TabPane tabId="suppliers">
                        <MultiSelect
                            name = {"suppliers"}
                            handleChange = { this.props.setModalValues }
                            data = {
                                RenameKeys(
                                    ObjectToArray(this.props.suppliers),
                                    ['name','name'],
                                    ['value','label']
                                )
                            }
                        />
                    </TabPane>
                    <TabPane tabId="code">
                        <Row>
                            <Col sm="12">
                                <FormGroup>
                                    <Label for="projectinput2">Բարկոդ</Label>
                                    <Input type="text" id="projectinput2"   name="lname" />
                                </FormGroup>
                            </Col>
                        </Row>
                    </TabPane>
                    <TabPane tabId="description">
                        <Row>
                            <Col sm="12">
                                <FormGroup>
                                    <Label for="projectinput8">Նկարագրություն</Label>
                                    <Input type="textarea" id="projectinput8" rows="5"  name="comment" />
                                </FormGroup>
                            </Col>
                        </Row>
                    </TabPane>
                </TabContent>
            </div>
        );
    }
}

export default TabsBorderBottom;
