import React from "react";
import {Button, FormGroup, Label, Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import Translate from "../../../Translate";
import {сategoryActions} from "../../../redux/categories/actions";

const CategoryModal = (props) => {
    function handleInputChange(key, value) {
        let new_value = {}
        new_value[key] = value
        props.setModalValues(new_value)
    }
    function handleClick() {
        props.сategoryActions(props.type,props.category)
    }
    function ModalBodyComponent(props) {
        if(props.type==="delete"){
            return(
                <ModalBody>Դուք համոզված ե՞ք ջնջել</ModalBody>
            )

        }else{
            return(
                <ModalBody>
                    <FormGroup>
                        <Label for="name"><Translate name="name"/></Label>
                        <input
                            className="form-control"
                            type="text"
                            id="name"
                            value={props.category.name}
                            onChange={event => handleInputChange("name", event.target.value)}
                            required
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="name_en"><Translate name="name_en"/></Label>
                        <input
                            className="form-control"
                            type="text"
                            id="name_en"
                            value={props.category.name_en}
                            onChange={event => handleInputChange("name_en", event.target.value)}
                            required
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="name_ru"><Translate name="name_ru"/></Label>
                        <input
                            className="form-control"
                            type="text"
                            id="name_ru"
                            value={props.category.name_ru}
                            onChange={event => handleInputChange("name_ru", event.target.value)}
                            required
                        />
                    </FormGroup>
                </ModalBody>
            )

        }

    }

    return (
        <React.Fragment>
            <Modal isOpen={props.modal[props.type]} toggle={()=>props.toggleModal(props.type)}  size="md">
                <ModalHeader toggle={()=>props.toggleModal(props.type)}><Translate name="addCategory"/></ModalHeader>
                <ModalBodyComponent {...props}/>
                <ModalFooter>
                    <Button color="primary" type="submit" onClick={handleClick}>
                        <Translate name="confirm"/>
                    </Button>
                </ModalFooter>
            </Modal>

        </React.Fragment>
    );

};

export default CategoryModal;
