import React, {Component} from "react";
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, Input, Row, Col, FormGroup} from "reactstrap";
import GroupTabContent from "../group/subGroupMenu";
import Translate from "../../../Translate";
import {List} from "react-feather"
import LocalizeTab from "../../localize/localizeTab";

class ModalExample extends Component {
    state = {
        modal: false
    };

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        });
        this.props.setModalValues("groups",this.props.selected)
    }

    render() {
        console.log(this.props.selected)
        return (
            <div>
                <Row>
                    <Col sm="11" className="mr-0">
                        <Input type="text" readonly="readonly"  placeholder = {this.props.name}/>
                    </Col>
                    <Col sm="1" className="ml-0">
                        <Button className="gradient-crystal-clear" onClick={this.toggle}><List size={16}/></Button>{" "}
                    </Col>

                </Row>


                <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle}
                    className={this.props.className}
                >
                    <ModalHeader toggle={this.toggle}>{this.props.name}</ModalHeader>
                    <ModalBody>
                        <GroupTabContent
                            data={this.props.data}
                            lang={this.props.lang}
                            handleOpen={this.props.handleOpen}
                            selectGroup={this.props.selectGroup}
                            selected={this.props.selected}
                            page="products"
                        />
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.toggle} >
                            <Translate name="select"/>
                        </Button>
                    </ModalFooter>
                </Modal>

            </div>

        );
    }
}

export default ModalExample;
