import {GET_ALL_CURRENCY_FAIL, GET_ALL_CURRENCY_REQUEST, GET_ALL_CURRENCY_SUCCESS} from "./actionTypes";

const initialState = {
    currency: null,
    loading: false,
    success: false,
    fail: false,
    isOpen: true,
    errors: {},
    setCurrency: {
        name: '1',
        short: '2',
        value: '3sdds',
    }
}

export default function currencyReducer(state = initialState, action) {

    switch (action.type) {

        case GET_ALL_CURRENCY_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_ALL_CURRENCY_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_ALL_CURRENCY_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                currency: action.result.results,
                errors: {},
            };
        default: return state;
    }
}
