import {
    SET_ACTIVE_LANGUAGE
} from "./actionTypes";
import { initialize,addTranslation,setActiveLanguage } from 'react-localize-redux';
import { translations } from './translation'
import { languages } from "./languages";

const INIT_STATE = {

};
initialize(languages, { defaultLanguage: 'fr' })
addTranslation(translations)


export default (state = languages, action) => {

    switch (action.type) {
        case SET_ACTIVE_LANGUAGE:
            setActiveLanguage('en')
            return {
                loading: true,
            };
        default:
            return {...state};
    }
};
