import {ADD_FILE} from "./actionTypes";

const initialState = {
    exel: false,
    file: null,
    errorType: null,
    uploadText: '',
    formData: {
        departOne : {
            sku: '1',
            name: '2',
            points: '3',
            measurement: '4'
        },
        departTwo : {
            classifiers: '5',
            suppliers: '6',
            barcode: '7',
            description: '8',
        },
    }
};

export default function importReducer(state = initialState, action) {

    switch (action.type) {

        case ADD_FILE:
            return {
                ...state,
                [action.name]: action.value
            }
        default: return state;
    }
}
