import React from "react";
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'react-bootstrap-table/dist/react-bootstrap-table-all.min.css';

function afterSearch(searchText, result) {
    console.log('Your search text is ' + searchText);
    console.log('Result is:');
    for (let i = 0; i < result.length; i++) {
        console.log('Fruit: ' + result[i].id + ', ' + result[i].name + ', ' + result[i].price);
    }
}
function onAfterDeleteRow(rowKeys) {
    alert('The rowkey you drop: ' + rowKeys);
}

const options = {
    afterDeleteRow: onAfterDeleteRow , // A hook for after droping rows.
    afterSearch: afterSearch  // define a after search hook
};

// If you want to enable deleteRow, you must enable row selection also.
const selectRowProp = {
    mode: 'checkbox'
};

class PaginationTable extends React.Component {
    render() {
        console.log(this.props.data)
        return (
            <div>
                <BootstrapTable
                    data={this.props.data}
                    pagination
                    search={ true }
                    options={ options }
                    selectRow={ selectRowProp }
                    deleteRow={ true }
                >
                    <TableHeaderColumn dataField='id' isKey>#</TableHeaderColumn>
                    <TableHeaderColumn dataField='key'>{this.props.dataKey}</TableHeaderColumn>
                    <TableHeaderColumn dataField='value'>{this.props.value}</TableHeaderColumn>
                    <TableHeaderColumn dataField='language'>{this.props.lang}</TableHeaderColumn>
                    <TableHeaderColumn dataField='button'>{this.props.buttons}</TableHeaderColumn>
                </BootstrapTable>
            </div>
        );
    }
}

export default PaginationTable
