import React  from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { getUsers,getUser,getPositions,addUser,addModal,editModal,setModalValues } from "../../redux/users/actions";
import { Button, Col, Card, CardBody, Modal, ModalHeader } from "reactstrap";
import TableComponent from './userTable';
import UserModal from './userModal';
import Translate from "../../Translate";



const UserContainer = (props) => {

        props.getUsers()
        props.getPositions()


    function toggle(key) {
        this[key+'Modal'](key)
    }

        return(
            <Col sm="12">
                <Card>
                    <CardBody>
                        <Button
                            color="primary"
                            type="button"
                            className="btn btn-raised btn-danger my-2 shadow-z-2"
                            onClick={() => toggle("add")}
                        >
                            <Translate name="add"/>
                        </Button>{" "}
                        <TableComponent {...props}/>
                    </CardBody>
                </Card>
                {/*<Modal isOpen={props.modal.add} toggle={toggle("add")}  size="md">*/}
                {/*    <ModalHeader toggle={toggle("add")}><Translate name="addContact"/></ModalHeader>*/}
                {/*    <UserModal {...props}/>*/}
                {/*</Modal>*/}
                {/*<Modal isOpen={props.modal.edit} toggle={toggle("edit")}  size="md">*/}
                {/*    <ModalHeader toggle={toggle("edit")}><Translate name="editContact"/></ModalHeader>*/}
                {/*    <UserModal {...props}/>*/}
                {/*</Modal>*/}

            </Col>
        )
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            getUsers,
            getUser,
            addUser,
            getPositions,
            addModal,
            editModal,
            setModalValues
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        users: state.users.users,
        modal: state.users.modal,
        positions: state.users.positions,
        user: state.users.user

    }
};
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer);
