import React from "react";
import {Modal, ModalHeader, Table} from "reactstrap";
import {Edit, Trash2} from 'react-feather';
import Translate from "../../Translate";
import UserModal from "./userModal";

const UserTable = (props) => {

    function toggle(type,id) {
        props.userModal("edit",!props.modal.edit)
        if(id){
            props.getUser(id)
        }
    }

    if (props.users.length) {
        return (
            <Table responsive>
                <thead>
                <tr>
                    {Object.keys(props.users[0]).map((key) => <td key={key}><Translate name={key}/></td>)}
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {props.users.map((value, index) =>
                    <tr key={index}>
                        {Object.keys(value).map((key) => <td key={key}>{value[key]}</td>)}
                        <td>
                            <Edit size={18} className="mr-2" onClick={() => toggle(value['id'],"edit")}/>
                            <Trash2 size={18} color="#FF586B" onClick={() => toggle(value['id'],"delete")}/>
                        </td>
                    </tr>
                )}
                </tbody>
                <Modal isOpen={this.props.modal.edit} toggle={()=>this.toggle("edit")}  size="md">
                    <ModalHeader toggle={()=>this.toggle("edit")}><Translate name="editContact"/></ModalHeader>
                    <UserModal {...this.props}/>
                </Modal>
                <Modal isOpen={this.props.modal.delete} toggle={()=>this.toggle("delete")}  size="md">
                    <ModalHeader toggle={()=>this.toggle("delete")}><Translate name="deleteContact"/></ModalHeader>
                    <UserModal {...this.props}/>
                </Modal>
            </Table>

        );
    } else {
        return (
            <div></div>
        )
    }
}
export default UserTable;
