import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { getUsers,getUser,getPositions,addUser,userModal,setModalValues } from "../../redux/users/actions";
import { Button, Col, Card, CardBody, Modal, ModalHeader } from "reactstrap";
import TableComponent from './userTable';
import UserModal from './userModal';
import Translate from "../../Translate";



class UserContainer extends Component {
    constructor(props) {
        super(props)
        this.props.getUsers()
        this.props.getPositions()

    }
    toggle(key) {
        this.props.userModal(key,!this.props.modal[key])
        console.log(this.props.modal.add)
    }

    render() {
        return(
            <Col sm="12">
                <Card>
                    <CardBody>
                        <Button
                            color="primary"
                            type="button"
                            className="btn btn-raised btn-danger my-2 shadow-z-2"
                            onClick={()=>this.toggle("add")}
                        >
                            <Translate name="add"/>
                        </Button>{" "}
                        <TableComponent {...this.props}/>
                    </CardBody>
                </Card>
                <Modal isOpen={this.props.modal.add} toggle={()=>this.toggle("add")}  size="md">
                    <ModalHeader toggle={()=>this.toggle("add")}><Translate name="addContact"/></ModalHeader>
                    <UserModal {...this.props}/>
                </Modal>

            </Col>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            getUsers,
            getUser,
            addUser,
            getPositions,
            userModal,
            setModalValues
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        users: state.users.users,
        modal: state.users.modal,
        positions: state.users.positions,
        user: state.users.user

    }
};
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer);
