import React from "react";
import {Button, Col, FormGroup, Label, Modal, ModalBody, ModalFooter, ModalHeader} from "reactstrap";
import Translate from "../../../Translate";

const CurrencyModal = props => {

    return (
        <Modal
            isOpen={props.isOpen}
            toggle={props.toggleModal()}
        >
            <ModalHeader>
                <Translate name={'editCurrency'}/>
            </ModalHeader>
            <ModalBody>
                <form>
                    <FormGroup row>
                        <Label for={'name'} sm={3}>
                            <Translate name={'name'}/>

                        </Label>
                        <Col sm={7} className={'pr-0'}>
                            <input
                                id={'name'}
                                type={'text'}
                                required={true}
                                className='form-control square'
                                placeholder={'US dollar'}
                                name={'name'}
                                value={props.setCurrency.name}
                            />
                        </Col>
                    </FormGroup>

                    <FormGroup row>
                        <Label for={'short'} sm={3}>
                            <Translate name={'short'}/>

                        </Label>
                        <Col sm={7} className={'pr-0'}>
                            <input
                                id={'short'}
                                type={'text'}
                                name={'short'}
                                required={true}
                                className='form-control square'
                                placeholder={'USD'}
                                value={props.setCurrency.short}
                            />
                        </Col>
                    </FormGroup>

                    <FormGroup row>
                        <Label for={'value'} sm={3}>
                            <Translate name={'value'}/>

                        </Label>
                        <Col sm={7} className={'pr-0'}>
                            <input
                                id={'value'}
                                type={'number'}
                                required={true}
                                className='form-control square'
                                placeholder={524}
                                min={0}
                                name={'value'}
                                value={props.setCurrency.value}
                            />
                        </Col>
                    </FormGroup>
                </form>
            </ModalBody>
            <ModalFooter>
                <Button
                    color="primary"
                    className="btn-square"
                    outline type="submit"
                >
                    <Translate name={'confirm'}/>
                </Button>
            </ModalFooter>
        </Modal>
    )
}

export default CurrencyModal
