import React from "react";
import {Form,Col, Row, FormGroup, Label, Button, ModalBody, ModalFooter, Input, Modal, ModalHeader,CustomInput} from "reactstrap";
import Translate from "../../../Translate";
import TabComponent from "./productTab";
import photo6 from "../../../assets/images/empty_product.svg";

const ProductModal = (props) => {
    function modalBodyContent() {
        if(props.type==="delete"){
            return(
                <ModalBody>Դուք համոզված ե՞ք ջնջել</ModalBody>
            )

        }else{
            return(
                <ModalBody>
                    <Row>
                        <Col md="12">
                            <div className="px-3">
                                <Form className="form-horizontal">
                                    <div className="form-body">
                                        <Row>
                                            <Col md="6">
                                                <FormGroup>
                                                    <img className="img-fluid" src={photo6} alt="Timeline 2" />
                                                    <Input type="file"  className="form-control-file"
                                                    />
                                                </FormGroup>
                                            </Col>
                                            <Col md="6">
                                                <FormGroup row>
                                                    <Label for="sku" sm={3}>ԱՊՄ</Label>
                                                    <Col sm={9}>
                                                        <Input type="text" id="sku"   name="sku" />
                                                    </Col>
                                                </FormGroup>
                                                <FormGroup row>
                                                    <Label for="sku" sm={3}>Անվանում</Label>
                                                    <Col sm={9}>
                                                        <Input type="text" id="name"   name="sku" />
                                                    </Col>
                                                </FormGroup>
                                                <FormGroup>
                                                    <Label for="projectinput1">Անվանում</Label>
                                                    <Input type="text" id="projectinput1"   name="fname" />
                                                </FormGroup>
                                                <FormGroup check className="px-0">
                                                    <CustomInput type="checkbox" id="exampleCustomCheckbox" label="Ակտիվ" defaultChecked />
                                                </FormGroup>

                                            </Col>

                                        </Row>
                                        <TabComponent />
                                    </div>
                                </Form>
                            </div>
                        </Col>

                    </Row>


                </ModalBody>
                )

        }

    }

    return (
        <React.Fragment>
            <Modal isOpen={props.modal[props.type]} toggle={()=>props.toggleModal(props.type)}  size="lg">
                <ModalHeader toggle={()=>props.toggleModal(props.type)}><Translate name="addProduct"/></ModalHeader>
                {modalBodyContent()}
                <ModalFooter>
                    <Button color="primary"  outline type="submit" onClick={()=>props.productActions(props.type,props.productModal)}>
                        <Translate name="confirm"/>
                    </Button>
                </ModalFooter>
            </Modal>

        </React.Fragment>
    );

};

export default ProductModal;
