import React, {useState} from 'react';
import {
    Button,
    ButtonGroup,
    Card,
    CardBody,
    Col,
    Collapse,
    ListGroup,
    ListGroupItem,
    Row
} from 'reactstrap';
import SubGroupMenu from "./subGroupMenu"
import classes from './content.module.css'
import {ChevronDown, ChevronRight} from "react-feather";
import PlusButton from "../../../components/buttons/plusButton";
import EditButton from "../../../components/buttons/editButton";
import DeleteButton from "../../../components/buttons/deleteButton";
import * as Icon from "react-feather"
import TextFields from "../../../components/textFieldUI/textField";
import Translate from "../../../Translate";
import CustomizedTreeView from "../../../components/treeUi/tree";

const ContentTable = props => {
    console.log(props.groups)

    const clickHandler = id => {
        props.getSeletGroup(id)
        props.getSubGroup(id)
    }

    return (
        <>
            <header className={classes.header}>
                {
                    props.groups ?
                        props.groups.map(
                            item => {

                                return (
                                    <React.Fragment key={item.id}>
                                        <Button
                                            active={
                                                props.selectGroup.id === item.id ?
                                                    true
                                                    :
                                                    false
                                            }
                                            className={classes.groupBtn}
                                            outline
                                            color='primary'
                                            onClick={
                                                () => clickHandler(item.id)
                                            }
                                        >
                                            {item.name}
                                        </Button>
                                    </React.Fragment>
                                )
                            }
                        )
                        :
                        null
                }
            </header>
            <nav className={classes.nav}>
                <div className={classes.controllers}>
                    {
                        props.group.id ?
                            <PlusButton/>
                            :
                            null
                    }
                    {
                        props.group.id ?
                            <EditButton
                                perm={props.perm}
                                onClick={() => {
                                    props.toggleModal('edit', props.group.id);
                                    props.groupActions("get", props.group)
                                }}
                            />
                            :
                            null
                    }
                    {
                        props.group.id ?
                            <DeleteButton
                                perm={props.perm}
                                onClick={
                                    () => props.toggleModal('delete', props.group.id)
                                }
                            />
                            :
                            null
                    }
                </div>
                <TextFields
                    label={<Translate name={'Search'}/>}
                />
            </nav>
            <section className={classes.section}>
                <CustomizedTreeView
                    name={props.group.name}
                />
            </section>
        </>
    )
}

export default ContentTable
