import React, {Component} from "react";
import {Col, Card, CardBody, CardHeader, Row} from "reactstrap";
import UploadButton from "../../../components/buttons/upploadBtnUI";
import AttachFileIcon from '@material-ui/icons/AttachFile';
import CustomizedSnackbars from "../../../components/snachbarsUI/snachbarsUi";

class ImportPage extends Component {
    state = {
        exel: false,
        file: null,
        errorType: null,
        uploadText: ''
    }

    handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }

        this.setState({
            exel: false,
            uploadText: '',
            errorType: null
        })
    };

    importExelHandler = event => {
        let file = event.target.files[0];
        if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {
            this.setState({
                exel: true,
                file: event.target.files[0],
                uploadText: 'your file uploaded successfully',
                errorType: 'success'
            })
        } else {
            this.setState({
                exel: true,
                uploadText: 'file type is not supported, make sure your file is Excel',
                errorType: 'error'
            })
        }

    }

    render() {
        console.log(this.state.exel)
        return (
            <Row>
                <Col sm={12}>
                    <Card>
                        <CardHeader>
                            <UploadButton
                                icon={<AttachFileIcon/>}
                                variant={'contained'}
                                color={'primary'}
                                multiple={false}
                                onChange={this.importExelHandler}
                                accept={'".csv, application/vnd.openxmlformats-officedocument.spreadsheetml.sheet, application/vnd.ms-excel'}
                            />
                        </CardHeader>
                        <CardBody>
                        </CardBody>
                        {
                            this.state.exel ?
                                <CustomizedSnackbars
                                    open={true}
                                    severity={this.state.errorType}
                                    text={this.state.uploadText}
                                    handleClose={this.handleClose}
                                />
                                :
                                null
                        }
                    </Card>
                </Col>
            </Row>
        )
    }
}

export default ImportPage
