export const sidebarImage = img => ({
    type: 'BG_IMAGE',
    img
})

export const setImage = () => ({
    type: 'BG_IMAGE_URL',
})

export function sidebarImageUrl(image) {

    return dispatch => {
        localStorage.setItem('image', image);
        dispatch(setImage())
    }
}

export const setColor = color => ({
    type: 'BG_COLOR',
    color
})

export function sidebarBgColor(color) {
    return dispatch => {
        localStorage.setItem('bgColor', color)
        dispatch(setColor())
    }
}

export const setCollapsed = () => ({
    type: 'SIDEBAR_COLLAPSED',
})

export function sidebarCollapsed(collapsed) {

    return dispatch => {
        localStorage.setItem('type', collapsed)
        dispatch(setCollapsed())
    }
}

export const sidebarSize = size => ({
    type: 'SIDEBAR_SIZE',
    size
})
