import React, {Component} from "react";
import {connect} from "react-redux";
import {bindActionCreators} from "redux";
import {setTranslations} from './redux/lang/actions'
import SessionStorage from "./services/SessionStorage";


class Translate extends Component {

    componentDidMount() {
        console.log(typeof this.props.name, this.props.name);
        if (!this.props.translations[this.props.name]) {
            if (this.props.translations.length > 0) {
                let index = false;
                let lang = SessionStorage.get('lang') || "am";
                for (let item of this.props.translations) {
                    if (item.key === this.props.name.toLowerCase() && item.value === this.props.name && item.language === lang) {
                        index = true;
                    }
                }
                if (index === false) {
                    // this.props.setTranslations(this.props.name.toLowerCase(), this.props.name, lang);
                }
            }
        }
    }

    render() {

        return (
            <span>
                {
                    this.props.translations[this.props.name] ?
                        this.props.translations[this.props.name]
                        :
                        this.props.name
                }
        </span>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            setTranslations
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        translations: state.languages.translations,
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Translate);
