import React, {Component, useState} from "react";
import {Row, Col, CardBody} from "reactstrap";
import {ChevronDown, ChevronRight, Plus,PlusSquare, Edit, Trash2} from "react-feather";
import Translate from "../../../Translate";
import AddButton from "../../../components/buttons/addButton";
import EditButton from "../../../components/buttons/editButton";
import DeleteButton from "../../../components/buttons/deleteButton";

function SubMenu(props) {
    const [open, setOpen] = useState(false);
    function handleOpen() {
        console.log(open)
        setOpen(!open)
    }
    function SubMenuItem(props) {

        if (open && props.data) {
            return <SubMenu data={props.data}/>
        }
        return <div></div>
    }
    function Chevron(props) {

        if(props.data) {
            if (!open) {
                return <ChevronRight size={18} className="mr-2" onClick={() => handleOpen()}/>
            }
            return <ChevronDown size={18} className="mr-2" onClick={() => handleOpen()}/>
        }
        return <div></div>
    }

    if (props.data.length > 0) {
        return <ul className="list-group ">
            {props.data.map((value, index) => {
                return (
                    <li className={"list-group-item list-group-item-action  py-1"} >
                        <Row className="todo-list-group-item" >
                             <Col sm="9" md="9" onClick={() => handleOpen()}><Chevron data={value.children}/>{value.name}</Col>
                             <Col sm="3" md="3">
                                <Plus size={18} className="mr-2" style={{color: '#009598\n'}}/>
                                 <EditButton perm = {props.perm}/>
                                 <DeleteButton perm = {props.perm} />
                            </Col>
                        </Row>
                        <SubMenuItem data={value.children} />
                    </li>
                )
            })}

        </ul>

    } else {
        return <div></div>
    }
};

class ListGroupDisabled extends Component {
    constructor(props) {
        super(props)

    }

    render() {
        return (
            <div  >
                <AddButton perm = {this.props.perm} />{" "}
                <SubMenu data={this.props.data}/>
            </div>

        );
    }
}

export default ListGroupDisabled;
