import {
    ADD_MENU_REQUEST,
    ADD_MENU_FAIL,
    ADD_MENU_SUCCESS,
    EDIT_MENU_REQUEST,
    EDIT_MENU_FAIL,
    EDIT_MENU_SUCCESS,
    DELETE_MENU_REQUEST,
    DELETE_MENU_FAIL,
    DELETE_MENU_SUCCESS,
    TOGGLE_MENU_MODAL,
    GET_MENU_REQUEST,
    GET_MENU_FAIL,
    GET_MENU_SUCCESS
} from "./actionTypes";
let cols =  'id,name,icon';
let col = 'id, name';

export const menuActions = (type, data) => {

    switch (type) {

        case 'get':
            return {
                type: [GET_MENU_REQUEST, GET_MENU_FAIL, GET_MENU_SUCCESS],
                promice: apiClient => apiClient.gett('pages/', {cols}),
            }
        case 'add':
            return {
                type: [ADD_MENU_REQUEST, ADD_MENU_FAIL, ADD_MENU_SUCCESS],
                promise: apiClient => apiClient.postt('pages/', data, {col})
            };
        case 'edit': return {
            type: [EDIT_MENU_REQUEST, EDIT_MENU_FAIL, EDIT_MENU_SUCCESS],
            promise: apiClient => apiClient.put(`pages/${data.id}`, data, {param:{id:data.id},col})
        }
        case 'delete': return {
            type: [DELETE_MENU_REQUEST, DELETE_MENU_FAIL, DELETE_MENU_SUCCESS],
            promise: apiClient => apiClient.delete(`pages/${data.id}`, {param:{id:data.id},col})
        }
        default: return ;
    }
}

export const toggleModal = (modalType, id) => {

    let obj = {"id": id}
    return {
        type: TOGGLE_MENU_MODAL,
        modalType,
        obj
    }

}
