import React, {Component} from "react";
import {
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
import ReactCountryFlag from "react-country-flag";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { set_active_language } from "../../redux/lang/actions";

class ThemeNavbar extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        let props = this.props.localize;
        return (
            <UncontrolledDropdown nav inNavbar className="pr-1">
                <DropdownToggle nav>
                    <ReactCountryFlag code="us" svg/> EN
                </DropdownToggle>
                <DropdownMenu right>
                    {props.languages.map((value, index) =>
                        <DropdownItem key={value.code} onClick={() => {
                            setLanguage(value.code)
                        }}>
                            <ReactCountryFlag code={value.code} svg/> {value.name}
                        </DropdownItem>
                    )}
                </DropdownMenu>
            </UncontrolledDropdown>

        );
    }
}

function mapStateToProps(state) {
    return state;
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            setLanguage
        },
        dispatch
    );
};

export default connect(mapStateToProps, mapDispatchToProps)(ThemeNavbar);
