import React, { useState } from 'react';
import { UncontrolledDropdown,DropdownItem, DropdownMenu, DropdownToggle } from 'reactstrap';
import Translate from "../../Translate";

const DropdownComponent = (props) => {
    const [dropdownOpen, setDropdownOpen] = useState(false);

    const toggle = () => setDropdownOpen(prevState => !prevState);

    return (
        <UncontrolledDropdown isOpen={dropdownOpen} toggle={toggle} size="sm">
            <DropdownToggle caret aria-expanded={dropdownOpen} color={'danger'}>
            </DropdownToggle>
            <DropdownMenu>
                {props.data.map((value) => {
                    return <DropdownItem onClick={()=>{props.onChange(props.name,value.id)}}><Translate name={"generate"}/> {value.name}</DropdownItem>

                })}

            </DropdownMenu>
        </UncontrolledDropdown>

    );
}

export default DropdownComponent;
