const initialState = {
    sidebarBgColor: false
}

export default function sidebarBgColor(state = initialState, action) {

    switch (action.type) {

        case 'BG_COLOR':
            return {
                ...state, sidebarBgColor: localStorage.setItem('sidebarBgColor')
            };
        default: return state;
    }
}

