import {
    GET_CATEGORIES_REQUEST,GET_CATEGORIES_FAIL,GET_CATEGORIES_SUCCESS,
    GET_CATEGORY_REQUEST,GET_CATEGORY_FAIL,GET_CATEGORY_SUCCESS,
    ADD_CATEGORY_REQUEST,ADD_CATEGORY_FAIL,ADD_CATEGORY_SUCCESS,
    EDIT_CATEGORY_REQUEST,EDIT_CATEGORY_FAIL,EDIT_CATEGORY_SUCCESS,
    DELETE_CATEGORY_REQUEST,DELETE_CATEGORY_FAIL,DELETE_CATEGORY_SUCCESS,
    SET_MODAL_VALUES
} from "./actionTypes";


const INIT_STATE = {
    categories: [],
    modal: {},
    loading: false,
    success: false,
    fail: false,
    errors: {}
};

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_CATEGORIES_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_CATEGORIES_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_CATEGORIES_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                categories: JSON.parse(action.result.data),
                errors: {},
            };
        case GET_CATEGORY_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_CATEGORY_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_CATEGORY_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                CATEGORY: JSON.parse(action.result.data)[0],
                errors: {},
            };
        case ADD_CATEGORY_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case ADD_CATEGORY_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_CATEGORY_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: false,
                CATEGORYs: [
                    ...state.CATEGORYs,
                    JSON.parse(action.result.data)[0]

                ]
            };
        case EDIT_CATEGORY_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case EDIT_CATEGORY_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case EDIT_CATEGORY_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: false,
                CATEGORY: {},
                CATEGORYs: JSON.parse(action.result.data)
            };
        case DELETE_CATEGORY_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case DELETE_CATEGORY_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case DELETE_CATEGORY_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: false,
                CATEGORY: {},
                CATEGORYs: JSON.parse(action.result.data)
            };
        case ADD_MODAL:
            return {
                ...state,
                modal: {
                    add: action.modal,
                    edit: false,
                    delete: false
                },
                CATEGORY: {}
            };
        case EDIT_MODAL:
            return {
                ...state,
                modal: {
                    add: false,
                    edit: action.modal,
                    delete: false
                },
            };
        case DELETE_MODAL:
            return {
                ...state,
                modal: {
                    add: false,
                    edit: false,
                    delete: action.modal
                },
                CATEGORY: {}
            };
        case SET_MODAL_VALUES:
            return {
                ...state,
                CATEGORY: {
                    ...state.CATEGORY,
                    ...action.value
                },

            }

        default:
            return {...state};
    }
};
