import {
    GET_USERS_REQUEST,GET_USERS_FAIL,GET_USERS_SUCCESS,
    GET_USER_REQUEST,GET_USER_FAIL,GET_USER_SUCCESS,
    GET_POSITIONS_REQUEST,GET_POSITIONS_FAIL,GET_POSITIONS_SUCCESS,
    ADD_USER_REQUEST,ADD_USER_FAIL,ADD_USER_SUCCESS,
    USER_MODAL,EDIT_MODAL
} from "./actionTypes";

let cols =  'id,username,firstname,lastname';

export const getUsers = () => {
    return {
        types: [GET_USERS_REQUEST,GET_USERS_FAIL,GET_USERS_SUCCESS],
        promise: (apiClient) => apiClient.get(`User/Users`, JSON.stringify({ cols }))
    }
};

export const getUser = ( id ) => {
    return {
        types: [GET_USER_REQUEST,GET_USER_FAIL,GET_USER_SUCCESS],
        promise: (apiClient) => apiClient.get(`User/Users`, JSON.stringify({ param:{id}, cols }))
    }
};

export const getPositions = () => {
    return {
        types: [GET_POSITIONS_REQUEST,GET_POSITIONS_FAIL,GET_POSITIONS_SUCCESS],
        promise: (apiClient) => apiClient.get(`User/Position`, JSON.stringify({ cols:'id,name' }))
    }
}

export const addUser = (data) => {
    return {
        types: [ADD_USER_REQUEST,ADD_USER_FAIL,ADD_USER_SUCCESS],
        promise: (apiClient) => apiClient.post(`User/Users`,JSON.stringify({data,cols}))

    }
};


export const userModal = (modal) => {
    return {
        type: USER_MODAL,
        modal
    }
}
