import React from "react";
import {connect} from "react-redux";
import {bindActionCreators} from "redux";
import {setTranslations} from './redux/lang/actions'
import SessionStorage from "./services/SessionStorage";


function Translate(props) {
    const checkWord = translations => {
        if (translations[props.name]) {
            return translations[props.name]
        }
        if (translations.length > 0) {
            let index = false;
            let lang = SessionStorage.get('lang') || "am";
            console.log(lang)
            for (let item of translations) {
                if (item.key === props.name.toLowerCase() && item.value === props.name && item.language === lang) {
                    index = true;
                }
            }
            if (index === false) {
                console.log("index", index)
                // props.setTranslations(props.name.toLowerCase(), props.name, lang);
            }
        }
        return props.name;
    }
    return (
        <span>
            {checkWord(props.translations)}
        </span>
    );
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            setTranslations
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        translations: state.languages.translations,
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Translate);
