import React, {useState} from "react";
import {Col, Row, Label, CustomInput} from "reactstrap";
import {ChevronDown, ChevronRight} from 'react-feather';
import Translate from "../../../Translate";

const DropDownItemChild = (props) => {
    const [checked, setValue] = useState(props.checked);

    function handleChecked() {
        setValue(!checked)
    }

    return (
        <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={props.id}>
            <Row className="todo-list-group-item">
                <Col sm="1" md="1"><Label onClick={() => handleChecked()}><CustomInput type="checkbox" checked={checked}defaultChecked={checked}/></Label></Col>
                <Col sm="10" md="10" ><p className="mb-0 font-small-3"><Translate name={props.name}/></p></Col>

            </Row>
        </li>
    );
}

const DropDownChild = (props) => {
    const [checked, setValue] = useState(props.checked);
    const [open, setOpen] = useState(false);

    function handleChecked(id) {
        setValue(!checked)
        // props.handle(!checked,id)
        // props.checked=!checked
    }

    function handleOpen() {
        setOpen(!open)
    }

    function SubMenu(props) {
        if (!open) {
            return <ChevronRight size={18} className="mr-2" onClick={() => handleOpen()}/>
        }
        return <ChevronDown size={18} className="mr-2" onClick={() => handleOpen()}/>
    }

    return (
        <li className={"list-group-item list-group-item-action no-border  bg-lighten-4"} key={props.id}>
            <Row className="todo-list-group-item">
                <Col sm="1" md="1"><Label onClick={() => handleChecked(props.id)}><CustomInput type="checkbox" checked={checked} defaultChecked={checked}/></Label></Col>
                <Col sm="10" md="10" onClick={() => handleOpen()}><p className="mb-0 font-small-3"><Translate
                    name={props.name}/></p></Col>
                <Col sm="1" md="1">
                    <SubMenu/>
                </Col>
            </Row>
            <ul className={open?"list-group icheck-task":"list-group icheck-task d-none"}>
                {Object.keys(props.tools).map(function (key) {
                    if (props.tools[key].checked === undefined) {
                        props.tools[key].checked = true
                    }
                    return <DropDownChild id={key} key={key} name={props.tools[key].name}
                                              checked={props.tools[key].checked}/>
                })}
            </ul>
        </li>
    );
};

const PositionMenu = (props) => {
    let pages = props.pages;
    console.log(props)
    return (
        <ul className="list-group icheck-task">
            {Object.keys(pages).map(function (key) {
                let checked = true;
                if (pages[key].checked) {
                    checked = pages[key].checked
                }
                return <DropDownItemChild
                    id={key} key={key}
                    name={pages[key].name}
                    tools={pages[key].tools}
                    checked={checked}
                    handle = {props.handle}
                />
            })}
        </ul>
    );
};
export default PositionMenu;
