import React from "react";
import {Col, Row, FormGroup, Label, Button, ModalBody, ModalFooter, ModalHeader, Modal} from "reactstrap";
import Translate from "../../../Translate";
import {translationActions} from "../../../redux/lang/actions";

const menuModal = (props) => {
    function modalBodyContent() {
        if (props.type === "delete") {
            return (
                <ModalBody><Translate name={'Դուք համոզված ե՞ք ջնջել'} /></ModalBody>
            )
        } else {
            return (
                <ModalBody>
                    <Row>
                        <Col md="12">
                            <FormGroup>
                                <Label for="name"><Translate name="key"/></Label>
                                <input
                                    className={`form-control  ${props.errors.name ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="key"
                                    value={props.translation.key ? props.translation.key : ""}
                                    onChange={event => props.setTranslationsModal("key", event.target.value)}
                                />
                            </FormGroup>
                        </Col>
                        <Col md="12">
                            <FormGroup>
                                <Label for="name"><Translate name="value"/></Label>
                                <input
                                    className={`form-control  ${props.errors.name ? 'is-invalid' : ''}`}
                                    type="text"
                                    id="value"
                                    value={props.translation.value ? props.translation.value : ""}
                                    onChange={event => props.setTranslationsModal("value", event.target.value)}
                                />
                            </FormGroup>
                        </Col>
                    </Row>
                </ModalBody>
            )

        }

    }

    return (
        <React.Fragment>
            <Modal isOpen={props.modal[props.type]} toggle={() => props.toggleTranslationModal(props.type, props.translation.id)}
                   size="md">
                <ModalHeader toggle={() => props.toggleTranslationModal(props.type, props.translation.id)}><Translate
                    name={props.type + "menu"}/></ModalHeader>
                {modalBodyContent()}
                <ModalFooter>
                    <Button
                        color="primary"
                        outline
                        type="submit"
                        onClick={
                            () => {
                                props.translationActions(props.type, props.translation);
                            }
                        }
                    >
                        <Translate name="confirm"/>
                    </Button>
                </ModalFooter>
            </Modal>
        </React.Fragment>
    );

};

export default menuModal;
