import React, {Component} from "react";
import {
    TabContent,
    TabPane,
    Nav,
    NavItem,
    NavLink,
    Row,
    Col, FormGroup, Label, Input,
    Dropdown,
    DropdownToggle,
    DropdownMenu

} from "reactstrap";
import classnames from "classnames";
import Translate from "../../../Translate";
import MultiSelect from "../../../components/select/multiSelect"
import {RenameKeys, ObjectToArray} from "../../../utility/utils";
import GroupModal from "./groupModal";

class TabsBorderBottom extends Component {

    constructor(props) {
        super(props)
        this.state = {
            activeTab: "code",
            modal: false
        };
        this.props.supplierActions("getAll");
        this.props.groupActions("getAll");

    }

    toggle1 = () => {
        this.setState({
            modal: !this.state.modal
        });
    }
    toggle = tab => {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    };

    render() {
        return (
            <div>
                <Nav tabs className="nav-border-bottom">
                    {Object.keys(this.props.groups).map((key) => {
                        return <NavItem key={key}>
                            <NavLink
                                className={classnames({
                                    active: this.state.activeTab === key
                                })}
                                onClick={() => {
                                    this.toggle(key);
                                }}
                            >
                                {this.props.groups[key].name[this.props.lang.active]}
                            </NavLink>
                        </NavItem>
                    })}

                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "suppliers"
                            })}
                            onClick={() => {
                                this.toggle("suppliers");
                            }}
                        >
                            <Translate name={"suppliers"}/>
                        </NavLink>
                    </NavItem>

                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "code"
                            })}
                            onClick={() => {
                                this.toggle("code");
                            }}
                        >
                            <Translate name={"code"}/>
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink
                            className={classnames({
                                active: this.state.activeTab === "description"
                            })}
                            onClick={() => {
                                this.toggle("description");
                            }}
                        >
                            <Translate name={"description"}/>
                        </NavLink>
                    </NavItem>
                </Nav>
                <TabContent activeTab={this.state.activeTab}>
                    {Object.keys(this.props.groups).map((key) => {
                        return (
                            <TabPane tabId={key} key={key}>
                                <GroupModal
                                    data={this.props.groups[key].subGroup}
                                    name={this.props.groups[key].name[this.props.lang.active]}
                                    lang={this.props.lang}
                                    handleOpen={this.props.handleOpen}
                                    selectGroup={this.props.selectGroup}
                                    selected={this.props.selectedGroups}
                                    setModalValues={this.props.setModalValues}
                                    product={this.props.product}
                                    id={key}
                                />


                            </TabPane>
                        )
                    })}

                    <TabPane tabId="suppliers">
                        <MultiSelect
                            name={"suppliers"}
                            handleChange={this.props.setModalValues}
                            data={
                                RenameKeys(
                                    ObjectToArray(this.props.suppliers),
                                    ['name', 'name'],
                                    ['value', 'label']
                                )
                            }
                        />
                    </TabPane>
                    <TabPane tabId="code">
                        <Row>
                            <Col sm="12">
                                <FormGroup>
                                    <Label for="barcode">Բարկոդ</Label>
                                    <div className="input-group">
                                        <Input
                                            type="text"
                                            id="barcode"
                                            value={this.props.product.barcode ? this.props.product.barcode || '' : ''}
                                            onChange={event => this.props.setModalValues("barcode", event.target.value)}
                                        />
                                        <div className="input-group-append">
                                            <Input type="select"  className='rounded-right'>
                                                {this.props.barcodeTypes.map((type) => {
                                                    return <option value={type.id}>{type.name}</option>

                                                })}
                                            </Input>
                                        </div>
                                        <Dropdown isOpen={dropdownOpen} toggle={toggle}>
                                            <DropdownToggle
                                                tag="span"
                                                data-toggle="dropdown"
                                                aria-expanded={dropdownOpen}
                                            >
                                                Custom Dropdown Content
                                            </DropdownToggle>
                                            <DropdownMenu>
                                                <div onClick={toggle}>Custom dropdown item</div>
                                                <div onClick={toggle}>Custom dropdown item</div>
                                                <div onClick={toggle}>Custom dropdown item</div>
                                                <div onClick={toggle}>Custom dropdown item</div>
                                            </DropdownMenu>
                                        </Dropdown>
                                    </div>
                                </FormGroup>
                            </Col>
                        </Row>
                    </TabPane>
                    <TabPane tabId="description">
                        <Row>
                            <Col sm="12">
                                <FormGroup>
                                    <Label for="description">Նկարագրություն</Label>
                                    <Input
                                        type="textarea"
                                        id="description"
                                        rows="5"
                                        value={this.props.product.description ? this.props.product.description || '' : ''}
                                        onChange={event => this.props.setModalValues("description", event.target.value)}
                                    />
                                </FormGroup>
                            </Col>
                        </Row>
                    </TabPane>
                </TabContent>

            </div>
        );
    }
}

export default TabsBorderBottom;
