import {
    GET_GROUPS_REQUEST,GET_GROUPS_FAIL,GET_GROUPS_SUCCESS,
    GET_GROUP_REQUEST,GET_GROUP_FAIL,GET_GROUP_SUCCESS,
    ADD_GROUP_REQUEST,ADD_GROUP_FAIL,ADD_GROUP_SUCCESS,
    EDIT_GROUP_REQUEST,EDIT_GROUP_FAIL,EDIT_GROUP_SUCCESS,
    DELETE_GROUP_REQUEST,DELETE_GROUP_FAIL,DELETE_GROUP_SUCCESS,
    ADD_SUB_GROUP_REQUEST,ADD_SUB_GROUP_FAIL,ADD_SUB_GROUP_SUCCESS,
    EDIT_SUB_GROUP_REQUEST,EDIT_SUB_GROUP_FAIL,EDIT_SUB_GROUP_SUCCESS,
    DELETE_SUB_GROUP_REQUEST,DELETE_SUB_GROUP_FAIL,DELETE_SUB_GROUP_SUCCESS,
    SET_GROUP_MODAL,TOGGLE_GROUP_MODAL,TOGGLE_SUB_GROUP_MODAL
} from "./actionTypes";
import {IsRequiredField,IsRequiredFields} from "../../utility/utils";

const INIT_STATE = {
    groups: [],
    group:{},
    modal: {},
    required: ["name"],
    loading: false,
    success: false,
    fail: false,
    errors: {}
};


export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_GROUPS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                modal:{},
                errors: {},
            };
        case GET_GROUPS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                modal:{},
                fail: true,
            };
        case GET_GROUPS_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal:{},
                groups: JSON.parse(action.result.data),
                errors: {},
            };
        case GET_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                group: JSON.parse(action.result.data),
                errors: {},
            };
        case ADD_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors:IsRequiredFields(state.required,state.group,state.errors)
            }
        case ADD_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                groups: JSON.parse(action.result.data)
            };
        case EDIT_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: IsRequiredFields(state.required,state.group,state.errors)
            }
        case EDIT_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case EDIT_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                group: {},
                groups: JSON.parse(action.result.data)
            };
        case DELETE_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case DELETE_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case DELETE_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                group: {},
                groups: JSON.parse(action.result.data)
            };
        case ADD_SUB_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors:IsRequiredFields(state.required,state.group,state.errors)
            }
        case ADD_SUB_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_SUB_GROUP_SUCCESS:
            let data =  JSON.parse(action.result.data);
            console.log(data)
            console.log(state.groups[data.group_id])
            state.groups[data.group_id].subGroup = data.subGroup
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                groups: state.groups
            };
        case EDIT_SUB_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: IsRequiredFields(state.required,state.group,state.errors)
            }
        case EDIT_SUB_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case EDIT_SUB_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                group: {},
                groups: JSON.parse(action.result.data)
            };
        case DELETE_SUB_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case DELETE_SUB_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case DELETE_SUB_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                group: {},
                groups: JSON.parse(action.result.data)
            };

        case SET_GROUP_MODAL:
            state.group[action.key] = action.value;
            return {
                ...state,
                errors:IsRequiredField(state.required,action,state.errors)
            };
        case TOGGLE_GROUP_MODAL:

            state.modal[action.modalType] = !state.modal[action.modalType];
            if(action.obj){
                if(action.modalType==="edit"){
                    state.group[action.obj.key] = action.obj.value
                }else{
                    state.group = action.obj
                }
            }

            return {
                ...state,
                errors: {}
            }
        case TOGGLE_SUB_GROUP_MODAL:

            state.subModal[action.modalType] = !state.modal[action.modalType];
            if(action.obj){
                if(action.modalType==="edit"){
                    state.group[action.obj.key] = action.obj.value
                }else{
                    state.group = action.obj
                }
            }

            return {
                ...state,
                errors: {}
            }

        default:
            return {...state};
    }
};
