import {
    GET_CATEGORIES_REQUEST,GET_CATEGORIES_FAIL,GET_CATEGORIES_SUCCESS,
} from "./actionTypes";

const INIT_STATE = {
    categories: [],
    modal: {},
    loading: false,
    success: false,
    fail: false,
    errors: {}
};

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_CATEGORIES_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_CATEGORIES_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_CATEGORIES_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                categories: JSON.parse(action.result.data),
                errors: {},
            };

        default:
            return {...state};
    }
};
