import React, { useState } from "react";
import {Badge} from "reactstrap";
import {connect} from "react-redux";

function LocalizeTab(props){
    const [code, setCode] = useState('am');
    const handleClick = code =>{
        this.setState({code})
    }
        return (
                <div className="badge-toolbar">

                    {props.languages.map(function (lang) {
                        return <Badge  color={code === lang.code ? "primary" : "light" } onClick = {()=>handleClick(lang.code)}>
                            {lang.name}
                        </Badge>
                    })}
                </div>
        );
}

const mapStateToProps = state => {
    return {
        languages: state.languages.languages,

    }
};

export default connect(mapStateToProps)(LocalizeTab);
