import {
    GET_USERS_REQUEST, GET_USERS_FAIL, GET_USERS_SUCCESS,
    ADD_USERS_REQUEST, ADD_USERS_FAIL, ADD_USERS_SUCCESS,
    GET_POSITIONS_REQUEST, GET_POSITIONS_FAIL, GET_POSITIONS_SUCCESS,
    USER_MODAL
} from "./actionTypes";


const INIT_STATE = {
    users: [],
    user: {},
    positions: [],
    loading: false,
    success: false,
    fail: false,
    modal: false,
    errors: {}
};

export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_USERS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_USERS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_USERS_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                users: JSON.parse(action.result.data),
                errors: {},
            };
        case GET_POSITIONS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_POSITIONS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_POSITIONS_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                positions: JSON.parse(action.result.data),
                errors: {},
            };
        case ADD_USERS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case ADD_USERS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_USERS_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: false,
                users: [
                    ...state.users,
                    JSON.parse(action.result.data[0])

                ]
            };
        case USER_MODAL:
            return {
                ...state,
                modal: action.modal
            };

        default:
            return {...state};
    }
};
