import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import TreeView from '@material-ui/lab/TreeView';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import TreeItem from '@material-ui/lab/TreeItem';

const useStyles = makeStyles({
    root: {
        height: 216,
        flexGrow: 1,
        maxWidth: 400,
    },
});

const Menu = props => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState([]);
    const [selected, setSelected] = React.useState([]);

    const handleToggle = (event, nodeIds) => {
        setExpanded(nodeIds);
    };

    const handleSelect = (event, nodeIds) => {
        setSelected(nodeIds);
    };

    const treeItemRender = (id, group_id) => {

        /*let result = */props.data.map(
            item => {
                if (parseInt(item.parentId) === parseInt(id)) {
                    console.log(item)
                    let groupId = item.group_id ? item.group_id.id : false
                    if(parseInt(groupId) === parseInt(group_id)) {
                        return (
                            <TreeItem
                                key={item.id}
                                nodeId={`${item.id}`}
                                label={item.name}
                            >
                                {treeItemRender(item.id, item.group_id.id)}
                            </TreeItem>
                        )
                    } else {
                        return null
                    }
                } else {
                    return null
                }
            }
        )
    }

    return (
        <>
            {
                props.data && props.data.length > 0 ?

                    <TreeView
                        className={classes.root}
                        defaultCollapseIcon={<ExpandMoreIcon />}
                        defaultExpandIcon={<ChevronRightIcon />}
                        expanded={expanded}
                        selected={selected}
                        onNodeToggle={handleToggle}
                        onNodeSelect={handleSelect}
                    >
                        {
                            props.data.map(
                                (item) => {

                                    return item.parent_id.length === 0 ?
                                        <TreeItem
                                            key={item.id}
                                            nodeId={`${item.id}`}
                                            label={item.name}
                                        >
                                            {treeItemRender(item.id, item.group_id.id)}
                                        </TreeItem>
                                        :
                                        null
                                }
                            )
                        }

                    </TreeView>
                    :
                    null
            }
        </>
    )

    // if (props.data && props.data.length > 0) {
    //     return <ul className="list-group ">
    //         {props.data.map((value, index) => {
    //             return value.parent_id.length === 0 ?
    //                 <SubMenu
    //                     key={index}
    //                     data={value}
    //                     perm={props.perm}
    //                     actions={props.actions}
    //                     handleOpen={props.handleOpen}
    //                     selectGroup={props.selectGroup}
    //                     toggleSubModal={props.toggleSubModal}
    //                     selected={props.selected}
    //                     lang={props.lang}
    //                     page={props.page}
    //                 />
    //                 :
    //                 null
    //         })}
    //
    //     </ul>
    //
    // } else {
    //     return <div></div>
    // }
}


export default Menu;

