import {
    GET_USERS_REQUEST,
    GET_USERS_FAIL,
    GET_USERS_SUCCESS,
    ADD_USERS_REQUEST,
    ADD_USERS_FAIL,
    ADD_USERS_SUCCESS
} from "./actionTypes";

export const getUsers = () => {
    return {
        types: [GET_USERS_REQUEST,GET_USERS_FAIL,GET_USERS_SUCCESS],
        promise: (apiClient) => apiClient.get(`User/Users`, JSON.stringify({ cols:'id,username,firstname,lastname' }))
    }
};

export const getPositions = () => {
    return {
        types: [GET_POSITIONS_REQUEST,GET_POSITIONS_FAIL,GET_POSITIONS_SUCCESS],
        promise: (apiClient) => apiClient.get(`User/Users`, JSON.stringify({ cols:'id,username,firstname,lastname' }))
    }
}

export const addUser = (data) => {
    return {
        types: [ADD_USERS_REQUEST,ADD_USERS_FAIL,ADD_USERS_SUCCESS],
        promise: (apiClient) => apiClient.post(`User/Users`,JSON.stringify(data))

    }
};
