import { SET_ACTIVE_LANGUAGE } from "./actionTypes";



export const setLanguage = (lang) =>{
    localStorage.setItem('lang', lang)
    console.log(lang)
    return {
        type: SET_ACTIVE_LANGUAGE,
        lang

    }
};
