import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { getUsers,getPositions,addUser,addModal } from "../../redux/users/actions";
import {Button, Col, Card, CardBody, Modal, ModalHeader, Table} from "reactstrap";
import TableComponent from './userTable';
import AddUser from './userModal';
import Translate from "../../Translate";
import {Edit, Trash2} from "react-feather";



class UserContainer extends Component {
    constructor(props) {
        super(props)
        this.props.getUsers()
        this.props.getPositions()
        // <td><Edit size={18} className="mr-2" id={value['id']}/> <Trash2 size={18} color="#FF586B"/></td>
    }
    toggle() {
        this.props.addModal(true)
    }

    render() {
        return(
            <Col sm="12">
                <Card>
                    <CardBody>
                        <Button
                            color="primary"
                            type="button"
                            className="btn btn-raised btn-danger my-2 shadow-z-2"
                            onClick={this.toggle.bind(this)}
                        >
                            <Translate name="add"/>
                        </Button>{" "}
                        <TableComponent data = {this.props.users}/>
                    </CardBody>
                </Card>
                <Modal isOpen={this.props.modal} toggle={this.toggle.bind(this)}  size="md">
                    <ModalHeader toggle={this.toggle.bind(this)}><Translate name="addContact"/></ModalHeader>
                    <AddUser props={this.props}/>
                </Modal>
            </Col>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            getUsers,
            addUser,
            getPositions,
            addModal
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        users: state.users.users,
        modal: state.users.modal,
        positions: state.users.positions,

    }
};
export default connect(mapStateToProps, mapDispatchToProps)(UserContainer);
