import {
    GET_GROUPS_REQUEST,GET_GROUPS_FAIL,GET_GROUPS_SUCCESS,
    GET_GROUP_REQUEST,GET_GROUP_FAIL,GET_GROUP_SUCCESS,
    ADD_GROUP_REQUEST,ADD_GROUP_FAIL,ADD_GROUP_SUCCESS,
    EDIT_GROUP_REQUEST,EDIT_GROUP_FAIL,EDIT_GROUP_SUCCESS,
    DELETE_GROUP_REQUEST,DELETE_GROUP_FAIL,DELETE_GROUP_SUCCESS,
    GET_SUB_GROUP_REQUEST,GET_SUB_GROUP_FAIL,GET_SUB_GROUP_SUCCESS,
    ADD_SUB_GROUP_REQUEST,ADD_SUB_GROUP_FAIL,ADD_SUB_GROUP_SUCCESS,
    EDIT_SUB_GROUP_REQUEST,EDIT_SUB_GROUP_FAIL,EDIT_SUB_GROUP_SUCCESS,
    DELETE_SUB_GROUP_REQUEST,DELETE_SUB_GROUP_FAIL,DELETE_SUB_GROUP_SUCCESS,
    SET_GROUP_MODAL,TOGGLE_GROUP_MODAL,TOGGLE_SUB_GROUP_MODAL,OPEN_MENU
} from "./actionTypes";
import {IsRequiredField,IsRequiredFields} from "../../utility/utils";

const INIT_STATE = {
    groups: [],
    group:{},
    modal: {},
    subModal: {},
    required: ["name"],
    loading: false,
    success: false,
    fail: false,
    errors: {}
};
function myFunction(arr,id,key,value=false){
    arr.forEach(function(current, index){
        if(current.id===id){
            current[key] = !current[key]
        }else{
            if(current.child){
                myFunction(current.child,id,key)
            }
        }
    })
    return arr;
}
function addGroup(arr,element,parent){
    arr.forEach(function(current, index){
        if(current.parent_id===parent) {
            element.push(element)
        }else{
            if(current.child){
                myFunction(current.child,element,parent)
            }
        }
    })
    return arr;
}
export default (state = INIT_STATE, action) => {
    switch (action.type) {
        case GET_GROUPS_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                modal:{},
                subModal: {},
                errors: {},
            };
        case GET_GROUPS_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                modal:{},
                subModal: {},
                fail: true,
            };
        case GET_GROUPS_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal:{},
                subModal: {},
                groups: JSON.parse(action.result.data),
                errors: {},
            };
        case GET_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                group: JSON.parse(action.result.data),
                errors: {},
            };
        case ADD_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors:IsRequiredFields(state.required,state.group,state.errors)
            }
        case ADD_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                groups: JSON.parse(action.result.data)
            };
        case EDIT_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: IsRequiredFields(state.required,state.group,state.errors)
            }
        case EDIT_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case EDIT_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                group: {},
                groups: JSON.parse(action.result.data)
            };
        case DELETE_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case DELETE_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case DELETE_GROUP_SUCCESS:
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                modal: {},
                group: {},
                groups: JSON.parse(action.result.data)
            };
        case GET_SUB_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: {},
            };
        case GET_SUB_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,
            };
        case GET_SUB_GROUP_SUCCESS:
            console.log(JSON.parse(action.result.data))
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                group: JSON.parse(action.result.data),
                errors: {},
            };
        case ADD_SUB_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors:IsRequiredFields(state.required,state.group,state.errors)
            }
        case ADD_SUB_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case ADD_SUB_GROUP_SUCCESS:
            let result =  JSON.parse(action.result.data);
            state.groups[result.group_id].subGroup = myFunction(state.groups[result.group_id].subGroup,result,result.parent_id)
            console.log(result)
            // state.groups[data.group_id].subGroup = data.subGroup;
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                subModal: {},
                // groups: state.groups
            };
        case EDIT_SUB_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
                errors: IsRequiredFields(state.required,state.group,state.errors)
            }
        case EDIT_SUB_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case EDIT_SUB_GROUP_SUCCESS:
            let data1 =  JSON.parse(action.result.data);
            console.log(data1)
            state.groups[data1.group_id].subGroup = data1.subGroup
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                subModal: {},
                group: {},
                groups: state.groups
            };
        case DELETE_SUB_GROUP_REQUEST:
            return {
                ...state,
                loading: true,
                success: false,
                fail: false,
            }
        case DELETE_SUB_GROUP_FAIL:
            return {
                ...state,
                loading: false,
                success: false,
                fail: true,

            };
        case DELETE_SUB_GROUP_SUCCESS:
            let data2 =  JSON.parse(action.result.data);
            state.groups[data2.group_id].subGroup = data2.subGroup
            return {
                ...state,
                loading: false,
                success: true,
                fail: false,
                subModal: {},
                group: {},
                groups: state.groups
            };

        case SET_GROUP_MODAL:
            state.group[action.key] = action.value;
            return {
                ...state,
                errors:IsRequiredField(state.required,action,state.errors)
            };
        case TOGGLE_GROUP_MODAL:
            state.modal[action.modalType] = !state.modal[action.modalType];
            if(action.obj){
                if(action.modalType==="edit"){
                    state.group[action.obj.key] = action.obj.value
                }else{
                    state.group = action.obj
                }
            }

            return {
                ...state,
                errors: {},
                subModal: {}
            }
        case TOGGLE_SUB_GROUP_MODAL:
            state.subModal[action.modalType] = !state.subModal[action.modalType];
            let obj = action.obj
            if(obj){
                if(action.modalType==="edit"){
                    state.group = {
                        ...state.group,
                        ...obj
                    }
                }else{
                    state.group = obj
                }
            }

            return {
                ...state,
                errors: {},
                modal: {}
            }
        case OPEN_MENU:
            state.groups[action.group_id].subGroup = myFunction(state.groups[action.group_id].subGroup,action.id,"open")
            return {
                ...state,
                groups:  state.groups
            }
        default:
            return {...state};
    }
};
